mapper.select:
  select
  t.PK_TEMPLATE, t.STOP_FLAG, c.NAME_CLASS TMP_CLASS_NAME, t.NAME_TMP, u.NAME_USER CREATOR_NAME, to_char(t.CREATE_TIME,'yyyy-mm-dd hh24:mi:ss') CREATE_TIME_STRING, t.RANGE 
  from EMR_TEMPLATE t
  left join EMR_TMP_CLASS c on c.PK_TMP_CLASS = t.PK_TMP_CLASS
  left join SYS_USER u on u.PK_USER = t.CREATOR

	   and NAME_TMP like '%'||#{name}||'%' 
     and t.RANGE = #{range} 
     and t.PK_TMP_CLASS = #{type} 
     and t.STOP_FLAG = #{status}



      order by ${sort} ${sortOrder}

  select count(PK_TEMPLATE) from EMR_TEMPLATE

		 and NAME_TMP like '%'||#{name}||'%' 
		 and RANGE = #{range} 
		 and PK_TMP_CLASS = #{type} 
		 and STOP_FLAG = #{status}
